function color(e,r,t,o){selectedColours=["rgb("+e+","+r+","+t+")","rgb("+e+","+parseInt(r+o)+","+t+")","rgb("+e+","+r+","+parseInt(t+o)+")","rgb("+e+","+parseInt(r+o)+","+parseInt(t+o)+")","rgb("+e+","+parseInt(r+o)+","+parseInt(t+2*o)+")","rgb("+e+","+parseInt(r+2*o)+","+parseInt(t+o)+")","rgb("+e+","+parseInt(r+2*o)+","+parseInt(t+2*o)+")"]}function setColor(e,r,t){$(".titleHeader,.pixel,.tracker").css("background-color","rgb("+e+","+r+","+t+")")}function createGrid(e,r){for(i=0;i<e;i++){var t=document.createElement("div");for(t.className="pixelCol",j=0;j<r;j++){var o=document.createElement("div");o.className="pixel",t.appendChild(o)}document.getElementById("pixelGrid").appendChild(t)}}function colourChange(){$(".pixel").each(function(){Math.floor(10*Math.random())>7&&$(this).css("background-color",selectedColours[Math.floor(7*Math.random())])})}var colours=["#e55","#e56","#e65","#e66","#e76","#e67","#e77"],selectedColours=[];$(document).ready(function(){createGrid(8,8),window.sr=ScrollReveal(),sr.reveal(".description",{duration:1100,scale:1,viewFactor:.1}),sr.reveal("h1, h2, .tile",{duration:900,scale:1,viewFactor:.1}),$(".menuItem").hover(function(){$(this).next(".selection").toggleClass("hover")})});